import { Button } from '@material-ui/core';
import React, { useState } from 'react';
import MyGrid from 'util/LogiUtil/MyGrid';
import Axios from 'axios';
import Swal from 'sweetalert2';

function WorkOrderStatus(props) {
    const [list, setList] = useState([]);
    const [size, setSize] = useState('50vh');
    //const [selectedRows, setSelectedRows] = useState([]);
    const [grid, setGrid] = useState([]);

    const column = {
        columnDefs: [
            {
                headerName: 'Ïô∏Ï£ºÎ∞úÏ£ºÎ≤àÌò∏',
                field: 'outsourcNo',
                minWidth: 150,
                headerCheckboxSelection: false,
                headerCheckboxSelectionFilteredOnly: true,
                suppressRowClickSelection: true,
                checkboxSelection: true
            },
            { headerName: 'ÏÜåÏöîÎüâÏ∑®Ìï©Î≤àÌò∏', field: 'mrpGatheringNo' },
            { headerName: 'ÌíàÎ™©ÏΩîÎìú', field: 'itemCode' },
            { headerName: 'ÌíàÎ™©Î™Ö', field: 'itemName' },
            { headerName: 'Îã®ÏúÑ', field: 'unitOfMrpGathering' },
            { headerName: 'ÌïÑÏöîÏàòÎüâ', field: 'necessaryAmount' },
            { headerName: 'Ïô∏Ï£ºÏßÄÏãúÏàòÎüâ', field: 'outSourcAmount' },
            { headerName: 'Ïô∏Ï£ºÎã®Í∞Ä', field: 'unitPriceOfOutsourc' },
            { headerName: 'Ìï©Í≥ÑÏï°', field: 'sumPriceOfOutsourc' },
            { headerName: 'Íµ¨Îß§Ïó¨Î∂ÄÎ∞è ÏÉùÏÇ∞Ïó¨Î∂Ä', field: 'orderOrProductionStatus' },
            {
                headerName: 'ÏûëÏóÖÏôÑÎ£åÎêúÏàòÎüâ',
                minWidth: 50,
                field: 'actualCompletionAmount',
                editable: true,
                cellRenderer: function(params) {
                    if (params.value == null) {
                        return 'üì∑';
                    }
                    return 'üì∑' + params.value;
                }
            }
        ]
    };

    const onCellClicked = params => {
        setGrid(params);
    };

    const completionRegist = () => {
        if (grid.length === 0) {
            return;
        }
        grid.api.stopEditing();
        let selectedRows = grid.api.getSelectedRows();
        let selectedRow = selectedRows[0];
        if (selectedRow === undefined || selectedRows === undefined) {
            Swal.fire('ÏïåÎ¶º', 'ÏÑ†ÌÉùÌïú ÌñâÏù¥ ÏóÜÏäµÎãàÎã§.', 'info');
            return;
        }

        if (
            selectedRow.actualCompletionAmount === undefined ||
            selectedRow.actualCompletionAmount === '' ||
            selectedRow.actualCompletionAmount === null
        ) {
            Swal.fire('ÏïåÎ¶º', 'ÏûëÏóÖÏôÑÎ£åÏàòÎüâÏùÑ ÏûÖÎ†•ÌïòÏã≠ÏãúÏò§.', 'info');
            return;
        }
        let confirmMsg =
            'ÏûëÏóÖÏùÑ ÏôÑÎ£åÌï©ÎãàÎã§.</br>' +
            'ÏûëÏóÖÏùºÎ†®Î≤àÌò∏ : ' +
            selectedRow.outsourcNo +
            '</br>' +
            '<b>ÏûëÏóÖÏôÑÎ£åÎêúÏàòÎüâ : ' +
            selectedRow.actualCompletionAmount +
            '</b></br>';

        // o Îç∞Ïù¥ÌÑ∞ Ï†ÑÏÜ°
        Swal.fire({
            title: 'ÏûëÏóÖÏùÑ ÏôÑÎ£åÌïòÏãúÍ≤†ÏäµÎãàÍπå?',
            html: confirmMsg,
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            cancelButtonText: 'Ï∑®ÏÜå',
            confirmButtonText: 'ÌôïÏù∏'
        }).then(result => {
            if (result.isConfirmed) {
                Axios.get('http://localhost:8282/logi/outsourc/outInspectionCompletion', {
                    params: {
                        outsourcNo: selectedRow.outsourcNo,
                        actualCompletionAmount: selectedRow.actualCompletionAmount
                    }
                })
                    .then(response => {
                        grid.api.updateRowData({ remove: [selectedRow] });
                        Swal.fire('ÏÑ±Í≥µ!', 'ÏûëÏóÖÎì±Î°ùÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§.', 'success');
                    })
                    .catch(e => {
                        Swal.fire('Ïò§Î•ò', e, 'error');
                    });
            }
        });
    };

    const InspectionSearch = () => {
        Axios.get('http://localhost:8282/logi/outsourc/outInspectionInfoList')
            .then(response => {
                setList(response.data.gridRowJson);
            })
            .catch(e => {
                Swal.fire('Ïò§Î•ò!!!', e, 'error');
            });
    };

    return (
        <>
            <MyGrid
                column={column}
                title={'Ïô∏Ï£ºÏûêÏû¨Ïã§Ï†ÅÍ≤ÄÏÇ¨'}
                list={list}
                onCellClicked={onCellClicked}
                rowSelection="single"
                size={size}
            >
                <Button
                    variant="contained"
                    color="secondary"
                    style={{ marginRight: '1vh' }}
                    onClick={InspectionSearch}
                >
                    Ïô∏Ï£ºÏûêÏû¨Ïã§Ï†ÅÏ°∞Ìöå
                </Button>
                <Button
                    variant="contained"
                    color="secondary"
                    //style={{ marginTop: "1vh" }}
                    onClick={completionRegist}
                >
                    Ïô∏Ï£ºÏûêÏû¨Í≤ÄÏÇ¨ÏôÑÎ£å
                </Button>
            </MyGrid>
        </>
    );
}

export default WorkOrderStatus;
